$(document).ready(function () {
    $(".block16__slider").slick({
        centerMode: !1,
        arrows: !0,
        dots: !0,
        adaptiveHeight: !1,
        slidesToShow: 1,
        slidesToScroll: 1,
        speed: 1e3,
        easing: "ease",
        infinite: !0,
        initialSlide: 0,
        autoplay: !1,
        autoplaySpeed: 4e3,
        pauseOnFocus: !0,
        pauseOnHover: !0,
        pauseOnDotsHover: !0,
        draggable: !0,
        swipe: !0,
        variableWidth: !1
    })
    $("a[href='#block20']").on("click", function (event) {
        $([document.documentElement, document.body]).animate({
            scrollTop: $("#block20").offset().top
        }, 500);
    })
})

